import { Component } from '@angular/core';
import { VideoService } from '../services/video.service';

@Component({
  selector: 'app-upload',
  templateUrl: './upload.component.html',
  styleUrls: ['./upload.component.css']
})
export class UploadComponent {
  selectedFile: File | null = null;
  title: string = '';
  description: string = '';
  categories: string = '';

  constructor(private videoService: VideoService) {}

  onFileSelected(event: any): void {
    this.selectedFile = event.target.files[0];
  }

  onUpload(): void {
    if (this.selectedFile) {
      this.videoService.uploadVideo(this.selectedFile, this.title, this.description, this.categories).subscribe(response => {
        console.log('Upload success', response);
      });
    }
  }
}
<div class="container">
  <div class="mb-3">
    <label for="title">Title</label>
    <input type="text" id="title" [(ngModel)]="title" class="form-control">
  </div>
  <div class="mb-3">
    <label for="description">Description</label>
    <textarea id="description" [(ngModel)]="description" class="form-control"></textarea>
  </div>
  <div class="mb-3">
    <label for="categories">Categories</label>
    <input type="text" id="categories" [(ngModel)]="categories" class="form-control">
  </div>
  <div class="mb-3">
    <input type="file" (change)="onFileSelected($event)" class="form-control">
  </div>
  <button (click)="onUpload()" class="btn btn-primary">Upload Video</button>
</div>
